package leetcode.no5;

public class Solution {

    /**
     * 暴力解法
     * 时间复杂度 O(n^3)
     * 空间复杂度 O(n)
     */
    public String longestPalindrome(String s) {

        if (null == s || "".equals(s)) {
            return "";
        }

        int maxLen = -1;
        int maxLenIndex = -1;
        int len = s.length();

        int p = 0, q = 0;

        for (; p < len; p++) {
            for (q = p + 1; q <= len; q++) {
                if (isHuiwen(s.substring(p,q))) {
                    if (q - p > maxLen) {
                        maxLen = q - p;
                        maxLenIndex= p;
                    }
                }
            }
        }
        return s.substring(maxLenIndex, maxLenIndex + maxLen);
    }

    public boolean isHuiwen (String s) {
        int i = 0;
        char[] sArr = s.toCharArray();
        while (i < s.length()/2) {
            if (sArr[i] != sArr[sArr.length - 1 - i]) {
                return false;
            }
            i++;
        }
        return true;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        String s = "";
        System.out.println(s.length());
        System.out.println(solution.longestPalindrome(""));;
    }

}
